mapper.select:
select count(*) from TACCOMADATION where 1=1 
  
   and FS_REGIONNO = #{regionno,jdbcType=VARCHAR}
  
  
   and FS_STARLVL = #{starlvl,jdbcType=VARCHAR}
  
  
   and FS_NAME like like #{name,jdbcType=VARCHAR}||'%'
  
  
   and FS_ADDR = #{addr,jdbcType=VARCHAR}
  
  
   and FS_SPECIALITY = #{speciality,jdbcType=VARCHAR}
  
  
   and FI_STAT = #{stat,jdbcType=DECIMAL}

select * from ( 
  select ROWNUM as rowno, t.*  from TACCOMADATION t where 1=1 
  
   and FS_REGIONNO = #{regionno,jdbcType=VARCHAR}
  
  
   and FS_STARLVL = #{starlvl,jdbcType=VARCHAR}
  
  
   and FS_NAME like like #{name,jdbcType=VARCHAR}||'%'
  
  
   and FS_ADDR = #{addr,jdbcType=VARCHAR}
  
  
   and FS_SPECIALITY = #{speciality,jdbcType=VARCHAR}
  
  
   and FI_STAT = #{stat,jdbcType=DECIMAL}
  
  
    and ROWNUM <= #{endrow}
  
  ) tt 
  where 1=1
  
    and tt.rowno >= #{startrow}

SELECT A.*
  FROM TACCOMADATION A
 WHERE A.FS_REGIONNO IN(SELECT A.FS_REGIONNO
                          FROM TSCENIC A
                          WHERE 1 = 1
                          
                            
                              AND TRIM(A.FS_NO) IN
                              
                                #{listItem}
                              
                            
                          
                          )

  and FS_REGIONNO = #{regionno,jdbcType=VARCHAR}


 and FS_STARLVL = #{starlvl,jdbcType=VARCHAR}


 and FS_NAME like like #{name,jdbcType=VARCHAR}||'%'


 and FS_ADDR = #{addr,jdbcType=VARCHAR}


  and FS_SPECIALITY = #{speciality,jdbcType=VARCHAR}


  and FI_STAT = #{stat,jdbcType=DECIMAL}
