mapper.update:
	update TSYSDEP 


    		FI_DEPNO = #{depNo,jdbcType=INTEGER},


   	 		FS_DEPNAME = #{depName,jdbcType=VARCHAR},


           	FI_STAT = #{stat,jdbcType=INTEGER}


	where FI_DEPNO = #{depNo,jdbcType = INTEGER}

mapper.select:
SELECT * FROM (SELECT ROWNUM AS rowno, dep.* FROM( SELECT d.* FROM TSYSDEP d WHERE 1=1
AND FI_DEPNO = #{depno}
AND FS_DEPNAME like '%'||#{depname}||'%'
AND FI_STAT like '%'||#{stat}||'%'
ORDER BY FI_DEPNO ) dep
WHERE ROWNUM <= #{endrow}
)
WHERE rowno > #{startrow}
