mapper.select:
  	select count(*) from view_tordercustom
	     where 1=1

	        and FS_ORDER_ID = #{fsOrderID}


	        and FI_SEQ = #{fiSeq}


	        and FT_CREATDATE = to_date(#{ftCreatdate,jdbcType=DATE},'yyyy-MM-dd HH24:mi:ss'),

      select * from (select ROWNUM as rowno, t.* from 
		(select 

		    ,

		    , fi_genindex, fs_name, ft_startdate
		    from view_tordercustom order by FS_ORDER_ID) t

    	where 1=1

	        and FS_ORDER_ID = #{fsOrderID}


	        and FI_SEQ = #{fiSeq}


	        and FT_CREATDATE = to_date(#{ftCreatdate,jdbcType=DATE},'yyyy-MM-dd HH24:mi:ss'),


	        and ROWNUM <= #{endrow}

	      ) tt where 1=1

	        and tt.rowno > #{startrow}
