mapper.select:
SELECT
	COUNT(1)
FROM
	TRestaurant
WHERE
	1=1

	AND FS_NAME like '%'||#{name, jdbcType = VARCHAR}||'%'


	AND FS_REGIONNO like '%'||#{regionno,jdbcType = VARCHAR}||'%'


	AND FI_SCALE >= #{minScale,jdbcType = INTEGER}


	AND FI_SCALE <= #{maxScale,jdbcType = INTEGER}


	AND FS_SPECIAL = #{special,jdbcType = VARCHAR}


	AND FS_LVL = #{lvl,jdbcType = VARCHAR}


	AND FI_STAT = #{stat}

SELECT * FROM ( SELECT ROWNUM rowno, r.* from ( select FS_NO, FS_NAME, FS_REGIONNO, FS_ADDR, FS_MENU, FI_SCALE, FS_SPECIAL, FS_ATTENTION, FS_LVL, FI_STAT FROM TRestaurant WHERE 1=1
AND FS_NAME like '%'||#{name, jdbcType = VARCHAR}||'%'
AND FS_REGIONNO like '%'||#{regionno,jdbcType = VARCHAR}||'%'
AND FI_SCALE >= #{minScale,jdbcType = INTEGER}
AND FI_SCALE <= #{maxScale,jdbcType = INTEGER}
AND FS_SPECIAL = #{special,jdbcType = VARCHAR}
AND FS_LVL = #{lvl,jdbcType = VARCHAR}
AND FI_STAT = #{stat,jdbcType=INTEGER}
order by FS_NO) r
where ROWNUM <= #{endrow}
)
WHERE rowno > #{startrow}

	  SELECT A.*
        FROM trestaurant A
       WHERE 1 = 1
         
      
        AND FS_NAME like '%'||#{name, jdbcType = VARCHAR}||'%'
      
      
        AND FS_REGIONNO like '%'||#{regionno,jdbcType = VARCHAR}||'%'
      
      
        AND FI_SCALE >= #{minScale,jdbcType = INTEGER}
      
      
        AND FI_SCALE <= #{maxScale,jdbcType = INTEGER}
      
      
        AND FS_SPECIAL = #{special,jdbcType = VARCHAR}
      
      
        AND FS_LVL = #{lvl,jdbcType = VARCHAR}
      
      
        AND FI_STAT = #{stat,jdbcType=INTEGER}
